# Use software to covert into one line
string = '>Rosalind_>Rosalind_>Rosalind_>Rosalind_>Rosalind_>Rosalind_>Rosalind_>Rosalind_'
start = 0
#As long as there exist a '>' symbol after the start index
array_of_pos = []
while( string.find('>', start) != -1): # If > symbol not finded return -1 to end loop
    
    pos = string.find('>', start) # Find index of > symbols 
    #print(pos)
    
    array_of_pos.append(pos)
    
    #Start index is assigned as the pos + 1 to search for next '>' symbol    
    start = pos + 1

def calculate_gc(string):
    gc_amount = 0
    for base in string:
        if base == 'G' or base == 'C':
            gc_amount += 1
    return gc_amount / len(string)

maximum_gc = 0
maximum_gc_id = ""

for i in range(len(array_of_pos)):
    pos = array_of_pos[i]
    ID = string[pos + 1 : pos + 14]
    #print(ID)
    substring = ""
    #Substring is obtained
    if( i != len(array_of_pos) - 1): # Check not ended location
        substring = string[pos + 14 : array_of_pos[i + 1]]
    else:
        substring = string[pos + 14 :]
    #Gc is calculated
    gc_content = calculate_gc(substring)
    #Gc is checked to know whether it is the highest
    if(maximum_gc < gc_content):
        maximum_gc = gc_content
        maximum_gc_id = ID
    #print(gc_content)
print(maximum_gc_id)
print(maximum_gc * 100)